        setUserInput((prevInput)=>{
            return {
                ...prevInput,
                [id]:value
            }
        });

    }

    const errorHandler=()=>{
        setError(null)
    }
    return (

       <React.Fragment>
        {error && <ErrorModal title={error.title} message={error.message} onClick={errorHandler}/>} 
